import { db } from "./db";
import { users } from "@shared/schema";
import { hashPassword } from "./auth";
import { eq } from "drizzle-orm";

const SUPER_ADMIN_EMAIL = "admin@degustapizza.com.br";
const SUPER_ADMIN_PASSWORD = "SuperAdmin2025!";
const SUPER_ADMIN_NOME = "Degusta Pizza Admin";

async function seedSuperAdmin() {
  console.log("Verificando Super Admin...");

  const existingUser = await db
    .select()
    .from(users)
    .where(eq(users.email, SUPER_ADMIN_EMAIL))
    .limit(1);

  if (existingUser.length > 0) {
    console.log("Super Admin já existe. Atualizando role...");
    await db
      .update(users)
      .set({ role: "super_admin" })
      .where(eq(users.email, SUPER_ADMIN_EMAIL));
    console.log("Role atualizada para super_admin.");
    return;
  }

  const hashedPassword = await hashPassword(SUPER_ADMIN_PASSWORD);

  const [newUser] = await db
    .insert(users)
    .values({
      email: SUPER_ADMIN_EMAIL,
      password: hashedPassword,
      nome: SUPER_ADMIN_NOME,
      role: "super_admin",
      tenantId: null,
    })
    .returning();

  console.log(`Super Admin criado com sucesso!`);
  console.log(`Email: ${newUser.email}`);
  console.log(`Role: ${newUser.role}`);
  console.log(`ID: ${newUser.id}`);
}

seedSuperAdmin()
  .then(() => {
    console.log("Seed concluído.");
    process.exit(0);
  })
  .catch((error) => {
    console.error("Erro ao criar Super Admin:", error);
    process.exit(1);
  });

server/eta_cron.ts.content:
import { db } from "./db";
import { pedidos, motoboys, tenants, logsN8n } from "@shared/schema";
import { eq, and, isNotNull } from "drizzle-orm";
import { calcularETA, verificarForaDeRota, registrarAlertaForaRota, Coordenadas } from "./geo_service";
import { sendToN8n } from "./n8n-requester";

const ETA_THRESHOLD_MINUTES = 2;
const CRON_INTERVAL_MS = 5 * 60 * 1000;

interface PedidoEmTransito {
  id: string;
  tenantId: string;
  motoboyId: string | null;
  etaMinutos: number | null;
  etaCalculadoEm: Date | null;
  destinoLat: string | null;
  destinoLng: string | null;
  rotaPolyline: string | null;
  trackingData: any;
  enderecoEntrega: string | null;
}

async function recalcularETAPedidosEmTransito(): Promise<void> {
  const pedidosEmTransito = await db
    .select({
      id: pedidos.id,
      tenantId: pedidos.tenantId,
      motoboyId: pedidos.motoboyId,
      etaMinutos: pedidos.etaMinutos,
      etaCalculadoEm: pedidos.etaCalculadoEm,
      destinoLat: pedidos.destinoLat,
      destinoLng: pedidos.destinoLng,
      rotaPolyline: pedidos.rotaPolyline,
      trackingData: pedidos.trackingData,
      enderecoEntrega: pedidos.enderecoEntrega,
    })
    .from(pedidos)
    .where(
      and(
        eq(pedidos.status, "saiu_entrega"),
        isNotNull(pedidos.motoboyId)
      )
    );

  console.log(`[ETA Cron] Processando ${pedidosEmTransito.length} pedidos em trânsito`);

  for (const pedido of pedidosEmTransito) {
    try {
      await processarPedidoETA(pedido as PedidoEmTransito);
    } catch (error) {
      console.error(`[ETA Cron] Erro ao processar pedido ${pedido.id}:`, error);
    }
  }
}

async function processarPedidoETA(pedido: PedidoEmTransito): Promise<void> {
  if (!pedido.motoboyId || !pedido.destinoLat || !pedido.destinoLng) {
    return;
  }

  const motoboy = await db
    .select()
    .from(motoboys)
    .where(eq(motoboys.id, pedido.motoboyId))
    .limit(1);

  if (!motoboy[0] || !motoboy[0].lat || !motoboy[0].lng) {
    return;
  }

  const motoboyLat = parseFloat(motoboy[0].lat.toString());
  const motoboyLng = parseFloat(motoboy[0].lng.toString());
  const destinoLat = parseFloat(pedido.destinoLat);
  const destinoLng = parseFloat(pedido.destinoLng);

  const etaResult = await calcularETA(motoboyLat, motoboyLng, destinoLat, destinoLng);
  const novoETA = etaResult.etaMinutos;
  const etaAnterior = pedido.etaMinutos || novoETA;
  const diferencaETA = Math.abs(novoETA - etaAnterior);

  await db
    .update(pedidos)
    .set({
      etaMinutos: novoETA,
      etaCalculadoEm: new Date(),
      trackingData: {
        ...(typeof pedido.trackingData === "object" && pedido.trackingData
          ? pedido.trackingData
          : {}),
        ultimoETA: novoETA,
        trafegoAtual: etaResult.trafegoAtual,
        motoboyLat,
        motoboyLng,
      },
      updatedAt: new Date(),
    })
    .where(eq(pedidos.id, pedido.id));

  if (diferencaETA >= ETA_THRESHOLD_MINUTES) {
    console.log(
      `[ETA Cron] ETA alterado significativamente para pedido ${pedido.id}: ${etaAnterior} -> ${novoETA} min`
    );
    await notificarMudancaETA(pedido, novoETA, etaAnterior);
  }

  if (pedido.rotaPolyline) {
    const posicaoMotoboy: Coordenadas = { lat: motoboyLat, lng: motoboyLng };
    const foraRota = verificarForaDeRota(posicaoMotoboy, pedido.rotaPolyline, 200);

    if (foraRota) {
      console.log(`[ETA Cron] Motoboy fora da rota para pedido ${pedido.id}`);
      await registrarAlertaForaRota(
        pedido.tenantId,
        pedido.motoboyId,
        pedido.id,
        200
      );
    }
  }
}

async function notificarMudancaETA(
  pedido: PedidoEmTransito,
  novoETA: number,
  etaAnterior: number
): Promise<void> {
  try {
    const tenant = await db
      .select()
      .from(tenants)
      .where(eq(tenants.id, pedido.tenantId))
      .limit(1);

    if (!tenant[0]?.apiKeyN8n) {
      console.log(`[ETA Cron] Tenant ${pedido.tenantId} não possui chave N8N`);
      return;
    }

    const payload = {
      tipo: "eta_atualizado",
      pedidoId: pedido.id,
      etaAnterior,
      novoETA,
      diferencaMinutos: novoETA - etaAnterior,
      enderecoEntrega: pedido.enderecoEntrega,
      timestamp: new Date().toISOString(),
    };

    const resultado = await sendToN8n({
      tenantId: pedido.tenantId,
      endpoint: "eta_atualizado",
      payload,
    });

    await db.insert(logsN8n).values({
      tenantId: pedido.tenantId,
      tipo: "webhook_saida",
      endpoint: "eta_atualizado",
      payload,
      resposta: resultado.data || null,
      status: resultado.success ? "enviado" : "erro",
      erro: resultado.success ? null : resultado.error || null,
    });
  } catch (error) {
    console.error(`[ETA Cron] Erro ao notificar mudança de ETA:`, error);
  }
}

let cronInterval: NodeJS.Timeout | null = null;

export function iniciarCronETA(): void {
  if (cronInterval) {
    console.log("[ETA Cron] Cron já está em execução");
    return;
  }

  console.log(`[ETA Cron] Iniciando cron de ETA (intervalo: ${CRON_INTERVAL_MS / 1000}s)`);

  recalcularETAPedidosEmTransito();

  cronInterval = setInterval(async () => {
    try {
      await recalcularETAPedidosEmTransito();
    } catch (error) {
      console.error("[ETA Cron] Erro no cron de ETA:", error);
    }
  }, CRON_INTERVAL_MS);
}

export function pararCronETA(): void {
  if (cronInterval) {
    clearInterval(cronInterval);
    cronInterval = null;
    console.log("[ETA Cron] Cron de ETA parado");
  }
}

export { recalcularETAPedidosEmTransito };

server/dpt_calculator.ts.content:
import { db } from "./db";
import { historicoPreparo, pedidos, produtos } from "@shared/schema";
import { eq, and, desc, sql, gte, isNotNull } from "drizzle-orm";

export interface DPTResult {
  tempoPreparoEstimado: number;
  tempoEntregaEstimado: number;
  fatorFila: number;
  confianca: number;
  detalhes: {
    tempoBaseProdutos: number;
    ajusteFila: number;
    pedidosNaFila: number;
  };
}

export interface DPTRealtimeInfo {
  pedidoId: string;
  status: string;
  tempoPreparoEstimado: number;
  tempoRestante: number;
  progresso: number;
  atrasado: boolean;
  prioridadeOrdenacao: number;
}

const TEMPO_ENTREGA_BASE = 15;
const TEMPO_PREPARO_PADRAO = 15;
const MAX_FATOR_FILA = 2.0;
const PESO_HISTORICO = 0.7;
const PESO_ESTIMATIVA = 0.3;

export async function calcularDPT(
  tenantId: string,
  itens: Array<{ produtoId?: string; nome: string; quantidade: number }>
): Promise<DPTResult> {
  let tempoBaseProdutos = 0;
  const agora = new Date();
  const umaSemanaAtras = new Date(agora.getTime() - 7 * 24 * 60 * 60 * 1000);

  for (const item of itens) {
    let tempoProduto = TEMPO_PREPARO_PADRAO;

    if (item.produtoId) {
      const produto = await db
        .select()
        .from(produtos)
        .where(and(eq(produtos.id, item.produtoId), eq(produtos.tenantId, tenantId)))
        .limit(1);

      if (produto.length > 0 && produto[0].tempoPreparoEstimado) {
        tempoProduto = produto[0].tempoPreparoEstimado;
      }

      const historico = await db
        .select({
          avgReal: sql<number>`AVG(${historicoPreparo.tempoReal})`.as("avgReal"),
          count: sql<number>`COUNT(*)`.as("count"),
        })
        .from(historicoPreparo)
        .where(
          and(
            eq(historicoPreparo.tenantId, tenantId),
            eq(historicoPreparo.produtoId, item.produtoId),
            gte(historicoPreparo.createdAt, umaSemanaAtras),
            isNotNull(historicoPreparo.tempoReal)
          )
        );

      if (historico.length > 0 && historico[0].count > 0 && historico[0].avgReal) {
        tempoProduto = Math.round(
          PESO_HISTORICO * historico[0].avgReal + PESO_ESTIMATIVA * tempoProduto
        );
      }
    }

    tempoBaseProdutos += tempoProduto * item.quantidade;
  }

  tempoBaseProdutos = Math.max(tempoBaseProdutos, TEMPO_PREPARO_PADRAO);

  const pedidosNaFila = await db
    .select({ count: sql<number>`COUNT(*)`.as("count") })
    .from(pedidos)
    .where(
      and(
        eq(pedidos.tenantId, tenantId),
        sql`${pedidos.status} IN ('pendente', 'recebido', 'em_preparo', 'confirmado')`
      )
    );

  const numPedidosFila = pedidosNaFila[0]?.count || 0;

  const fatorFila = Math.min(1 + numPedidosFila * 0.1, MAX_FATOR_FILA);

  const ajusteFila = Math.round(tempoBaseProdutos * (fatorFila - 1));
  const tempoPreparoEstimado = tempoBaseProdutos + ajusteFila;

  const tempoEntregaEstimado = tempoPreparoEstimado + TEMPO_ENTREGA_BASE;

  let confianca = 85;
  if (numPedidosFila > 10) confianca -= 10;
  if (numPedidosFila > 20) confianca -= 10;
  if (tempoBaseProdutos > 60) confianca -= 5;

  return {
    tempoPreparoEstimado,
    tempoEntregaEstimado,
    fatorFila,
    confianca: Math.max(confianca, 50),
    detalhes: {
      tempoBaseProdutos,
      ajusteFila,
      pedidosNaFila: numPedidosFila,
    },
  };
}

export async function obterDPTRealtime(tenantId: string): Promise<DPTRealtimeInfo[]> {
  const agora = new Date();

  const pedidosAtivos = await db
    .select()
    .from(pedidos)
    .where(
      and(
        eq(pedidos.tenantId, tenantId),
        sql`${pedidos.status} IN ('pendente', 'recebido', 'em_preparo', 'confirmado')`
      )
    )
    .orderBy(desc(pedidos.createdAt));

  const resultado: DPTRealtimeInfo[] = [];

  for (const pedido of pedidosAtivos) {
    const tempoEstimado = pedido.tempoPreparoEstimado || TEMPO_PREPARO_PADRAO;
    const inicioRef = pedido.inicioPreparoAt || pedido.createdAt;
    const tempoDecorrido = Math.round(
      (agora.getTime() - new Date(inicioRef).getTime()) / 60000
    );

    const tempoRestante = Math.max(0, tempoEstimado - tempoDecorrido);
    const progresso = Math.min(100, Math.round((tempoDecorrido / tempoEstimado) * 100));
    const atrasado = tempoDecorrido > tempoEstimado;

    let prioridadeOrdenacao = tempoRestante;
    if (atrasado) prioridadeOrdenacao = -tempoDecorrido;
    if (pedido.status === "em_preparo") prioridadeOrdenacao -= 1000;

    resultado.push({
      pedidoId: pedido.id,
      status: pedido.status,
      tempoPreparoEstimado: tempoEstimado,
      tempoRestante,
      progresso,
      atrasado,
      prioridadeOrdenacao,
    });
  }

  resultado.sort((a, b) => a.prioridadeOrdenacao - b.prioridadeOrdenacao);

  return resultado;
}

export async function registrarInicioPreparoPedido(
  tenantId: string,
  pedidoId: string
): Promise<void> {
  const agora = new Date();

  await db
    .update(pedidos)
    .set({
      status: "em_preparo",
      inicioPreparoAt: agora,
      updatedAt: agora,
    })
    .where(and(eq(pedidos.id, pedidoId), eq(pedidos.tenantId, tenantId)));

  const pedido = await db
    .select()
    .from(pedidos)
    .where(and(eq(pedidos.id, pedidoId), eq(pedidos.tenantId, tenantId)))
    .limit(1);

  if (pedido.length > 0 && pedido[0].itens) {
    const itens = pedido[0].itens as Array<{
      produtoId?: string;
      nome: string;
      quantidade: number;
    }>;

    for (const item of itens) {
      const tempoProduto = TEMPO_PREPARO_PADRAO;

      if (item.produtoId) {
        const produto = await db
          .select()
          .from(produtos)
          .where(and(eq(produtos.id, item.produtoId), eq(produtos.tenantId, tenantId)))
          .limit(1);

        const tempoEst = produto.length > 0 && produto[0].tempoPreparoEstimado
          ? produto[0].tempoPreparoEstimado
          : TEMPO_PREPARO_PADRAO;

        await db.insert(historicoPreparo).values({
          tenantId,
          pedidoId,
          produtoId: item.produtoId,
          produtoNome: item.nome,
          tempoEstimado: tempoEst,
          inicioAt: agora,
        });
      } else {
        await db.insert(historicoPreparo).values({
          tenantId,
          pedidoId,
          produtoNome: item.nome,
          tempoEstimado: tempoProduto,
          inicioAt: agora,
        });
      }
    }
  }
}

export async function registrarFimPreparoPedido(
  tenantId: string,
  pedidoId: string
): Promise<void> {
  const agora = new Date();

  await db
    .update(pedidos)
    .set({
      status: "pronto",
      prontoEntregaAt: agora,
      updatedAt: agora,
    })
    .where(and(eq(pedidos.id, pedidoId), eq(pedidos.tenantId, tenantId)));

  const registros = await db
    .select()
    .from(historicoPreparo)
    .where(
      and(
        eq(historicoPreparo.pedidoId, pedidoId),
        eq(historicoPreparo.tenantId, tenantId)
      )
    );

  for (const registro of registros) {
    const tempoReal = Math.round(
      (agora.getTime() - new Date(registro.inicioAt).getTime()) / 60000
    );

    await db
      .update(historicoPreparo)
      .set({
        fimAt: agora,
        tempoReal,
      })
      .where(eq(historicoPreparo.id, registro.id));

    if (registro.produtoId) {
      await atualizarTempoEstimadoProduto(tenantId, registro.produtoId);
    }
  }
}

async function atualizarTempoEstimadoProduto(
  tenantId: string,
  produtoId: string
): Promise<void> {
  const umaSemanaAtras = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

  const historico = await db
    .select({
      avgReal: sql<number>`AVG(${historicoPreparo.tempoReal})`.as("avgReal"),
      count: sql<number>`COUNT(*)`.as("count"),
    })
    .from(historicoPreparo)
    .where(
      and(
        eq(historicoPreparo.tenantId, tenantId),
        eq(historicoPreparo.produtoId, produtoId),
        gte(historicoPreparo.createdAt, umaSemanaAtras),
        isNotNull(historicoPreparo.tempoReal)
      )
    );

  if (historico.length > 0 && historico[0].count >= 5 && historico[0].avgReal) {
    const produto = await db
      .select()
      .from(produtos)
      .where(and(eq(produtos.id, produtoId), eq(produtos.tenantId, tenantId)))
      .limit(1);

    if (produto.length > 0) {
      const tempoAtual = produto[0].tempoPreparoEstimado || TEMPO_PREPARO_PADRAO;
      const novoTempo = Math.round(
        PESO_HISTORICO * historico[0].avgReal + PESO_ESTIMATIVA * tempoAtual
      );

      await db
        .update(produtos)
        .set({ tempoPreparoEstimado: novoTempo })
        .where(eq(produtos.id, produtoId));
    }
  }
}

